sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   // Exit if form is still loading or if new value is empty
   if (isLoading || newValue === '') {
      return;
   }

   // Create a GlideAjax instance to call the 'lmsAjax' Script Include
   var ga= new GlideAjax('lmsAjax');

   // Add parameters: Script Include method name, current user ID, and the selected leave type
   ga.addParam('sysparm_name','getLeaveBucket');
   ga.addParam('sysparm_user',g_user.userID);
   ga.addParam('sysparm_leavetype',newValue);

   // Send the request and process the response with the getBucket function
   ga.getXML(getBucket);

   // Parse the response and set form fields with leave accrual data
   function getBucket(response){
      var answer = response.responseXML.documentElement.getAttribute("answer");
      var result= JSON.parse(answer);
      g_form.setValue('accrued',result.accrued);
      g_form.setValue('balance',result.balance);
      g_form.setValue('taken',result.taken);
   }
}
